@node_entry
def route_choice(player_choice: str) -> str:
    choice = player_choice.split(': ')[1] if ': ' in player_choice else player_choice

    if choice == 'Forest Path':
        return 'forest'
    elif choice == 'Mountain Trail':
        return 'mountain' 
    elif choice == 'Cave Entrance':
        return 'cave'
    else:
        return 'river'

