@node_entry
def end_adventure(adventure_result: str) -> str:
    print("\n=== ADVENTURE COMPLETED ===")
    print(f"Your adventure: {adventure_result}")
    print("Thank you for playing!")
    return f"Quest completed! {adventure_result}"

