def get_values(widgets):
    return {}

def set_values(widgets, outputs):
    result = outputs.get('output_1', 'No result')
    widgets['summary_text'].setPlainText(f'{result}\n\nYour adventure has concluded. Click Play Again to restart!')

# LIVE MODE: Play Again button restarts the entire graph
def setup_events(widgets, node_executor):
    def restart_game():
        widgets['summary_text'].setPlainText('Restarting adventure...')
        node_executor.restart_graph()
    widgets['play_again_btn'].clicked.connect(restart_game)
